import Head from "next/head";
import { ShopContext } from "../../context/shopContext";
// Sanity.io
import client from "../../client";
import urlFor from "../../imageUrlBuilder";

//Bootstrap
import Container from "react-bootstrap/Container";
import Image from "react-bootstrap/Image";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Spinner from "react-bootstrap/Col";

const Tops = (props) => {
  const { fetchCollection, collection } = React.useContext(ShopContext);

  React.useEffect(() => {
    let query = "Tops";
    fetchCollection(query);
    return () => {};
  }, [fetchCollection]);

  return (
    <div>
      <Head>
        <title>OJOS | Tops</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <h1 className="page-title">Tops</h1>
      <Container fluid="lg">
        <Row>
          {collection.products ? (
            collection.products.map((product) => (
              <Col xs={12} sm={6} md={6} lg={4} key={product.id}>
                <div className="product-section">
                  <a href={`/products/${product.handle}`}>
                    <Image
                      className="products"
                      fluid
                      src={product.images[0].src}
                    />
                  </a>
                </div>
                <div className="product-description">
                  <a
                    href={`/products/${product.handle}`}
                    className="product-name"
                  >
                    {product.title}
                  </a>
                  <a href="/products/:id" className="product-price">
                    {product.availableForSale
                      ? "$" + product.variants[0].price
                      : "Sold out"}
                  </a>
                </div>
              </Col>
            ))
          ) : (
            <div className="no-products-msg">
              <p>No products right now, check back later!</p>
            </div>
          )}
        </Row>
      </Container>
    </div>
  );
};

export default Tops;
